import React from 'react';
import './styles.css';
import {Avatar} from '../../components/main/avatar'
import 'bootstrap/dist/css/bootstrap.css'
import {connect} from 'react-redux'
import {fetchCategories} from '../../actions/index'


const BACK = 'http://127.0.0.1:8000/';

async function getCategoriesFromDB() {

}

export class App extends React.Component {
    constructor(props) {
        super(props);

        if (this.props.categoryList) {
            fetch('http://127.0.0.1:8000/api/categories/')
                .then(res => res.json())
                .then((data) => {
                    this.props.fetchCategories(data.categories);
                })
                .catch(console.log);
        } else {
            console.log(this.props.categoryList);
        }
    }

    render() {
        if(this.state) {
            return (
                <div className="App">
                    <header className="App-header">
                        <br/>
                        <br/>
                        {this.props.categoryList[0]}
                        <div className="card-deck">
                            {
                                this.state.data.categories.map((category, index) =>
                                    (<Avatar url={ category.url } imageSrc={ BACK + category.image } title={ category.name }
                                            description={ category.description }/>))
                            }
                        </div>
                        <br/>
                        <br/>
                    </header>
                </div>
            );
        }else
            return (
                <div className="App">
                    <header className="App-header">
                        {(this.props.categoryList) ? this.props.categoryList[0] : "NOO"}
                        <p>ЗАГРУЗКА</p>
                    </header>
                </div>
            );
    }
}

function mapStateToProps(state) {
    return {
        categoryList: state.categories.categories
    }
}

const mapDispatchToProps = {
    fetchCategories
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
