import React from 'react';
import './styles.css';
import {Avatar} from '../../components/main/avatar'
import 'bootstrap/dist/css/bootstrap.css'
import {connect} from 'react-redux'
import {fetchCategories} from '../../actions/index'


const BACK = 'http://127.0.0.1:8000/';

async function getCategoriesFromDB() {
    fetch('http://127.0.0.1:8000/api/categories/')
        .then(res => res.json())
        .then((data) => {
            return data.categories;
        })
        .catch(console.log);
}

export class App extends React.Component {
    constructor(props) {
        super(props);

        if (Object.keys(this.props.categoryList).length === 0) {

            getCategoriesFromDB().then((res) => console.log(res));

            fetch('http://127.0.0.1:8000/api/categories/')
                .then(res => res.json())
                .then((data) => {
                    console.log("fetch");
                    this.props.fetchCategories(data.categories);
                })
                .catch(console.log);
        } else {
            console.log("already here");
        }
    }

    render() {
        if(Object.keys(this.props.categoryList).length !== 0) {
            return (
                <div className="App">
                    <header className="App-header">
                        <br/>
                        <br/>
                        <div className="card-deck">
                            {
                                this.props.categoryList.map((category, index) =>
                                    (<Avatar url={ category.url } imageSrc={ BACK + category.image } title={ category.name }
                                            description={ category.description }/>))
                            }
                        </div>
                        <br/>
                        <br/>
                    </header>
                </div>
            );
        }else
            return (
                <div className="App">
                    <header className="App-header">
                        <p>ЗАГРУЗКА</p>
                    </header>
                </div>
            );
    }
}

function mapStateToProps(state) {
    return {
        categoryList: state.categories.categories
    }
}

const mapDispatchToProps = {
    fetchCategories
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
