import React from 'react';
import {List} from "../../components/compensations/list";
import {ItemList} from "../../components/compensations/itemList";
import './styles.css'
import {Avatar} from "../../components/main/avatar";

export class CompensationList extends React.Component {
    constructor(props) {
        super(props);
    }

    componentWillMount() {
        fetch('http://127.0.0.1:8000/api/compensations/?category_url=standard')
            .then(res => res.json())
            .then((data) => {
                this.setState({data: data})
            })
            .catch(console.log)
    }

    // Получение данных для всех ItemList должно приходить через БД из таблицы с этими компенсациями
    render() {
        if (this.state) {
            console.log(this.state.data.compensations[1].documents);
            return (
                <div className="Block">
                    <List items={
                        this.state.data.compensations.map((compensation, index) =>
                            (<ItemList category={"standard"}
                                       id={"drugs"}
                                       title={compensation.name}
                                       money={compensation.money.split("\n")}
                                       documents={compensation.requirements.split("\n")}
                                       addInfo={compensation.additional_info.split("\n")}
                                       isOnce={compensation.once_a_term}
                            />))
                    }/>
                </div>
            );
        } else {
            return(<div className="Block">Загрузка</div>);
        }
    }
}

CompensationList.propTypes = {
    category: String,
};

CompensationList.defaultProps = {
    category: "default"
};
